INSERT INTO `soal` (`id_soal`, `id_mapel`, `nomor`, `soal`, `jenis`, `pilA`, `pilB`, `pilC`, `pilD`, `pilE`, `jawaban`, `file`, `file1`, `fileA`, `fileB`, `fileC`, `fileD`, `fileE`) VALUES
	(27, 3, 1, 'Tujuan penciptaan langit dan bumi serta pergantian siang dan malam menurut Q.S Al Imran : 190. Adalah….', 1, 'Membuktikan bahwa Allah Swt itu maha adil dan perkasa', 'Membuktikan tentang tauhid, keesaan, dan kekuasaan Allah Swt', 'Membuktikan tentang kekuatan Allah dan manusia', 'Memberikan inspirasi untuk masuk agama Islam', 'Mencontohkan orang – orang yang soleh', 'B', '', '', '', '', '', '', ''),
	(28, 3, 2, 'Orang yang mampu berpikir kritis termasuk …..', 1, 'Ulul azmi', 'Ulul  Albab', 'Ikhlas', 'Al Amin', 'Qona\'ah', 'B', '', '', '', '', '', '', ''),
	(29, 3, 3, 'Tanda – tanda seseorang mengimani Hari Akhir, diantaranya adalah….', 1, 'Takut menghadapi kematian', 'Tidak mau menerima jabatan duniawi', 'Mengabaikan urusan dunia yang bersifat fana', 'Selalu berusaha ikhlas dalam melakukan pekerjaan', 'Selalu mengingat tanda – tanda datangnya Hari Akhir dengan baik', 'E', '', '', '', '', '', '', ''),
	(30, 3, 4, ' Setelah semua manusia dibangkitkan dari alam kubur, mereka dikumpulkan di padang yang maha luas yang disebut….', 1, 'Barzakh', 'Ba\'as', 'Mahsyar', 'Hisab', 'Jaza', 'C', '', '', '', '', '', '', ''),
	(31, 3, 5, 'Berikut ini tidak termasuk sikap seorang ulil albab yang tercantum dalam Q.S Ali Imran 3 :191 yaitu adalah…', 1, 'Merenungkan ciptaan Allah Swt', 'Mengingat Allah Swt dalam keadaan duduk', 'Menghafalkan ayat- ayat tertentu', 'Mengingat Allah Swt dalam keadaan berdiri', 'Mengingat Allah Swt dalam keadaan berbaring', 'C', '', '', '', '', '', '', ''),
	(32, 3, 6, 'Dalam surat luqman ayat 14, Allah Swt menginformasikan bahwa ibu menyapih anaknya pada usia...', 1, '2 Tahun', '3 Tahun', '4 Tahun', '5 Tahun', '1 Tahun', 'A', '', '', '', '', '', '', ''),
	(33, 3, 7, '    Setelah manusia meninggal dunia, mereka berada di alam pembatasan antara dunia dan akhirt yang disebut….', 1, 'Barzakh', 'Mahsyar', 'Ba\'as', 'Hisab', 'Jaza', 'A', '', '', '', '', '', '', ''),
	(34, 3, 8, ' Sikap yang tepat terhadap ayat al Qur’an adalah…', 1, 'Membacanya setiap malam jum’at dengan khusyuk', 'Membacanya dengan tartil dan suara bagus', 'Membaca dan mengkajinya bersama orang yang ahli', 'Membacanya dengan fasih dihadapan guru', 'Membacanya setiap saat untuk mendapat kelancaran usaha', 'C', '', '', '', '', '', '', ''),
	(35, 3, 9, 'Menyampaikan nasihat melalui ceramah termasuk kategori Dakwah...', 1, 'Dengan perbuatan', 'Dengan harta', 'Bil Mal', 'Bil Hal', 'Bil Lisan', 'E', '', '', '', '', '', '', ''),
	(36, 3, 10, 'Kematian setiap makhluk hidup dalam kehidupan fana disebut…..', 1, 'Sebuah bencana', 'Tanda penghakiman terakhir', 'Kiamat Sugra', 'Kiamat', 'Bencana', 'C', '', '', '', '', '', '', ''),
	(37, 3, 11, 'Pengorbanan seorang ibu untuk seorang anak yang dikisahkan dalam surah luqman ayat 14 yaitu…', 1, 'Melahirkan dengan susah payah', 'Mengandung dalam keadaan lemah', 'Mendidiknya dengan sunguh- sungguh', 'Menyusui hingga anak berusia dua tahun', 'Terjaga setiap malam untuk menjaga anak', 'B', '', '', '', '', '', '', ''),
	(38, 3, 12, 'Iman kepada hari akhir / Kiamat adalah rukun iman ke….', 1, 'Satu', 'Dua', 'Tiga', 'Empat', 'Lima', 'D', '', '', '', '', '', '', ''),
	(39, 3, 13, 'Peristiwa berikut yang merupkan kiamat kubra…', 1, 'Gunung meletus', 'Gempa', 'Tsunami', 'Banjir', 'Musnahnya alam smesta', 'E', '', '', '', '', '', '', ''),
	(40, 3, 14, 'Hari pembalasan amal manusia disebut yaumul…', 1, 'Hisab', 'Jaza', 'Mizan', 'Ba\'ts', 'Hasyr', 'B', '', '', '', '', '', '', ''),
	(41, 3, 15, 'Tanda- tanda kiamat ada yang besar dan ada yang kecil. Berikut ini tanda - tanda yang besar, kecuali….', 1, 'Munculnya dajjal', 'Matahari terbit dari barat', 'Banyak terjadi gempa bumi', 'Turunnya Nabu Isa', 'Lahirnya Imam Mahdi', 'C', '', '', '', '', '', '', ''),
	(42, 3, 16, 'Kehidupan yang hakiki adalah kehidupan di….', 1, 'Dunia', 'Akhirat', 'Langit', 'Alam kubur', 'Alam barzah', 'B', '', '', '', '', '', '', ''),
	(43, 3, 17, 'Hari kiamat juga disebut yaum al qiyamah yaitu hari….', 1, 'Penegakan keadilan', 'Yang paling akhir', 'Kegoncangan', 'Ancaman', 'Hari penimbangan', 'A', '', '', '', '', '', '', ''),
	(44, 3, 18, 'Sesungguhnya, pada penciptaan langit dan bumi ada tanda kekuasaan Allah bagi orang yang….', 1, 'Berakal', 'Sombong', 'Ingkar', 'Berbudaya', 'Berbudi', 'A', '', '', '', '', '', '', ''),
	(45, 3, 19, 'Secara garis besar surah Al Imran ayat 190 - 191 menjelaskan tentang….', 1, 'Musyawarah', 'Perilaku jujur', 'Husnuzan', 'Berpikir kritis', 'Sikap demokratis', 'D', '', '', '', '', '', '', ''),
	(46, 3, 20, 'Allah menjelaskan kepada kita bahwa sesungguhnya dalam penciptaan langit dan bumi, dan silih bergantinya siang dan malam terdapat tanda- tanda bagi orang yang….', 1, 'Munafik', 'Jujur', 'Berakal', 'Bertaqwa', 'Beriman', 'C', '', '', '', '', '', '', ''),
	(47, 3, 21, 'Berikut ini tidak termasuk sikap seorang ulil albab yang tercantum dalam Q.S Ali Imran 3 :191 yaitu adalah...', 1, 'Memikirkan rahasia ciptaan Allah Swt', 'Mengingat Allah Swt dalam keadaan duduk', 'Menghafalkan ayat- ayat tertentu', 'Mengingat Allah Swt dalam keadaan berdiri', 'Mengingat Allah Swt dalam keadaan berbaring', 'C', '', '', '', '', '', '', ''),
	(48, 3, 22, 'Dalam Surah lukman (31) ayat 13 yang disebut kezaliman yang besar yaitu…', 1, 'Meninggalkan zakat', 'Meninggalkan shalat fardu', 'Durhaka kepada orangtua', 'Meninggalkan puasa ramadhan', 'Menyekutukan Allah Swt', 'E', '', '', '', '', '', '', ''),
	(49, 3, 23, 'Dalam Surah Luqman ayat 13, dijelaskan bahwa menyembah kepada selain Allah SWT  termasuk perbuatan...', 1, 'Syirik', 'Zholim', 'Sesat', 'Bid\'ah', 'Laknat', 'B', '', '', '', '', '', '', ''),
	(50, 3, 24, 'Umat Islam yang menyekutukan Allah SWT, berarti tidak sungguh- sungguh dalam...', 1, 'Menunaikan zakat', 'Bersyahadat', 'Mengerjakan puasa', 'Membantu orang miskin', 'Memberi makan anak yatim', 'B', '', '', '', '', '', '', ''),
	(51, 3, 25, 'Salah satu kandungan Surah al Mu’min (40) ayat 59 yaitu….', 1, 'Hanya Allah Swt yang tidak binasa pada hari kiamat', 'Tidak ada yang mengetahui datangnya hari kiamat', 'Manusia akan dibangkitkan setelah hari kiamat', 'Kebanyakan manusia tidak beriman pada hari kiamat', 'Kiamat kubra ditandai dengan tiupan malaikat israfil', 'D', '', '', '', '', '', '', ''),
	(52, 3, 26, 'Surah at Takwir ayat 1 menjelaskan bahwa saat hari kiamat matahari akan…', 1, 'Digulung', 'Padam', 'Meledak', 'Menimpa bumi', 'Hancur berkeping- keping', 'A', '', '', '', '', '', '', ''),
	(53, 3, 27, 'Roh seorang mukmin setelah meninggal dunia akan berada di….', 1, 'Surga', 'Neraka', 'Alam barzah', 'Alam akhirat', 'Alam gaib', 'C', '', '', '', '', '', '', ''),
	(54, 3, 28, 'Setelah dibagkitkan dari kematian, manusia akan dikumpulkan di…', 1, 'Pintu surga', 'Pintu neraka', 'Alam barzah', 'Padang mahsyar', 'Sirat mustaqim', 'D', '', '', '', '', '', '', ''),
	(55, 3, 29, 'Arti “ ulil albab “ adalah...', 1, 'Umat Islam', 'Orang dewasa', 'Umat terdahulu', 'Orang yang berfikir', 'Orang dewasa', 'D', '', '', '', '', '', '', ''),
	(56, 3, 30, 'Orang yang menasehati juga harus memberi……', 1, 'Teladan', 'Sedekah', 'Saran', 'santunan', 'Pekerjaan', 'A', '', '', '', '', '', '', ''),
	(57, 3, 31, 'Q.S. Lauqman ayat ke 12 menjelaskan bahwa Luqmanul-hakim mendapatkan keistimewaan dari Alloh berupa Al-Hikmah, Al-Hikmah yang dimaksud pada ayat tersebut adalah ….', 1, 'Orang yang Sabar', 'Orang yang bersyukur', 'Orang yang Jujur', 'Orang yang Soleh', 'Orang yang Taqwa', 'B', '', '', '', '', '', '', ''),
	(58, 3, 32, 'Kisah-kisah yang melatarbelakangi turun salah satu ayat Al-Qur\'an disebut dengan ….', 1, 'Asbabul Kisoh', 'Asbabut Tarikh', 'Asbabun Nuzul', 'Asbabul Wurud', 'Asbabul Fatwa', 'C', '', '', '', '', '', '', ''),
	(59, 3, 33, 'Kisah-kisah yang melatarbelakangi turun salah satu Hadits Rosul disebut dengan ….', 1, 'Asbabul Kisoh', 'Asbabut Tarikh', 'Asbabun Nuzul', 'Asbabul Wurud', 'Asbabul Fatwa', 'D', '', '', '', '', '', '', ''),
	(60, 3, 34, 'Jika dalam bacaan Al-Qu\'an menemukan lafad ALLOH dan kata sebelumnya berharokah kasroh maka pada ilmu tajwid disebut ….', 1, 'Takbir', 'Tahqiq', 'Tahmid', 'Tafhim', 'Tarjih', 'B', '', '', '', '', '', '', ''),
	(61, 3, 35, 'Jika dalam bacaan Al-Qu\'an menemukan lafad ALLOH dan kata sebelumnya berharokah Fathah atau Dhomah maka pada ilmu tajwid disebut ….', 1, 'Takbir', 'Tahqiq', 'Tahmid', 'Tafhim', 'Tarjih', 'D', '', '', '', '', '', '', ''),
	(62, 3, 36, 'Dibawah ini yang bukan termasuk kaidah-kaidah mad thobi\'I adalah …', 1, 'Mad Thobi\'i Juga bisa disebut Mad Asli', 'Mad Thobi\'i terdiri dari 3 huruf yaitu alif, wau, dan ya\'', 'memiliki bobot bacaan sampai dengan 2 harokat', 'jika alif sukun sebelunya berharokat fathah', 'jika wau sukun sebelumnya berharokat Kasroh', 'E', '', '', '', '', '', '', ''),
	(63, 3, 37, 'Dalam kaidah membaca Al-Qur\'an harus memperhatikan makhorijul huruf dan tanda berhenti bacaan yang disebut dengan', 1, 'Waqof', 'Tasdid', 'Sukun', 'Fathah', 'Tanwin', 'A', '', '', '', '', '', '', ''),
	(64, 3, 38, 'Jika Mad asli dibaca 6 harkat pada akhir ayat disebut dengan ….', 1, 'Mad Iwad', 'Mad Arid li Sukun', 'Mad Badal', 'Mad Thobi\'i', 'Mad Silah Thowilah', 'B', '', '', '', '', '', '', ''),
	(65, 3, 39, 'Jika dalam bacaan Al-Qu\'an menemukan huruf qolqolah ditengah kalimah maka pada ilmu tajwid disebut ….', 1, 'Qolqolah tahlifi', 'Qolqolah \'ainy', 'Qolqolah Kubra\'', 'Qolqolah Sugra\'', 'Qolqolah Khalqi', 'D', '', '', '', '', '', '', ''),
	(66, 3, 40, 'Jika dalam bacaan Al-Qu\'an menemukan huruf qolqolah di akhir kalimah maka pada ilmu tajwid disebut ….', 1, 'Qolqolah tahlifi', 'Qolqolah \'ainy', 'Qolqolah Kubra\'', 'Qolqolah Sugra\'', 'Qolqolah Khalqi', 'C', '', '', '', '', '', '', ''),
	(67, 4, 1, 'Rumusan Pancasila yang resmi dan sah terdapat dalam …', 1, 'Pembukaan UUD 1945', 'Piagam Jakarta', 'Keputusan Presiden', 'Ketetapan MPR', 'BPUPKI', 'A', '', '', '', '', '', '', ''),
	(68, 4, 2, 'Pada sidang BPUPKI tanggal 29 Mei 1945-1 Juni 1945, Muhammad Yamin mengajukan usul dasar negara seperti berikut, kecuali …', 1, 'Peri kebangsaan ', 'Peri ketuhanan', 'Peri kemanusiaan', 'Peri keadilan', 'Peri kerakyatan', 'D', '', '', '', '', '', '', ''),
	(69, 4, 3, 'Sikap positif terhadap Pancasila sebagai pandangan hidup dalam kehidupan keluarga adalah …', 1, 'Menolak pembagian tugas', 'Menyelesaikan masalah dengan kekerasan', 'Menghargai pendapat antar anggota keluarga', 'Saling menyalahkan', 'Saling menghina', 'C', '', '', '', '', '', '', ''),
	(70, 4, 4, 'Hasil sidang ppki tanggal 18 agustus 1945, antara lain menetapkan bahwa untuk sementara waktu,presiden akan dibantu oleh …', 1, 'MPRS DAN BPAS', 'Komite Nasional', 'DPRS', 'Pemuda Nasional', 'Wakil presiden', 'B', '', '', '', '', '', '', ''),
	(71, 4, 5, 'Dalam sidang I BPUPKI,3 orang yang mendapatkan kesempatan untuk"mengemukakan pendapat mengenai dasar negara indonesia" yang akan di bentuk adalah ', 1, 'Muhammad yamin, Soerpardjo, dan Ir Soekarno', 'Muhammad Yamin, Soepomo, dan Ir Soekarno', 'Muhammad Natsir, Soepomo, dan Soekarno', 'Muhammad Natsir, Moh Hatta, dan Ir Soekarno', 'Dr. Radjiman, Moh Hatta, Muhammad Natsir', 'B', '', '', '', '', '', '', ''),
	(72, 4, 6, 'Rumusan dasar negara di dalam sidang BPUPKI pertama di sampaikan oleh ', 1, 'Mr. Muh Yamin', 'Ir. Soekarno', 'Prof. Soepomo', 'DRS. Moh Hatta', 'Dr. Radjiman', 'A', '', '', '', '', '', '', ''),
	(73, 4, 7, 'perumusan dasar negara indonesia di lakukan melalui sidang BPUPKI yang berlangsung pada tanggal', 1, '29 mei-1 junni 1945', '31 mei- 1 junni 1945', '30 mei-1 junni 1945', '31 mei-3 junni 1945', '20 juni-29 juli 1945', 'A', '', '', '', '', '', '', ''),
	(74, 4, 8, 'Pada 22 junni 1945 panitia 9 berhasil merumuskan rancangan pembukaan UUD 1945 yang kemudian dikenali sebagai ', 1, 'Dasasila bandung ', 'Pancasila', 'Piagam HAM ', 'Piagam jakarta', 'UUD 1945', 'D', '', '', '', '', '', '', ''),
